INSERT INTO mangas VALUES (1, 'Chainsaw Man', '06/08/2020', 2, 'https://cdn.discordapp.com/attachments/766013915536556056/1103970556052787210/csm_01.png', 0, 'Denji is a loser who will do literally anything for money, due to inheriting an enormous debt to some local Yakuza Loan Sharks after his father seemingly committed suicide when he was a child. This includes hunting Devils, dangerous Anthropomorphic Personifications of human fears that randomly appear in the world on a regular basis. Having no friends other than his pet Devil Pochita, a dog-like creature that is also a chainsaw, and having sold several of his own organs now to survive, Denji seemingly has no hope of a future. But even so, Denji still dreams of living a better life.', 6.5, 1, 'Tatsuki Fujimoto' )
INSERT INTO mangas VALUES (2, 'Berserk', '26/10/1990', 1, 'https://cdn.discordapp.com/attachments/766013915536556056/1103970555356516383/berserk_01.jpg', 0, 'Guts is a warrior haunted by his past, namely the tragedy that ended in the loss of his arm, his eye, and everyone he cared about. He is Walking the Earth seeking only one thing: revenge against Griffith, once his best friend, who treacherously sacrificed him and all their comrades to the demon gods known as the God Hand in order to be reborn as their fifth member. The world is crawling with Apostles, monstrous servants of the God Hand who gave up their humanity in exchange for power. By hunting these creatures, Guts hopes to pick up the trail of Griffith and make him pay.', 6.5, 2, 'Kentaro Miura')
INSERT INTO mangas VALUES (3, 'Jujutsu Kaisen', '21/01/2020', 2, 'https://cdn.discordapp.com/attachments/766013915536556056/1103970556644175912/jjk_01.png', 0, 'Yuji Itadori is an athletically blessed but easygoing high school student not sure of his path in life when, during a routine visit, his grandfather suddenly perishes, but not before making Yuji swear a promise to him: that he will do kind deeds so that he dies surrounded by loved ones, unlike his grandfather.', 6.5, 1, 'Gege Akutami')
INSERT INTO mangas VALUES (4, 'Tokyo Revengers', '17/05/2017', 2, 'https://cdn.discordapp.com/attachments/766013915536556056/1103970557336244245/tr_01.jpg', 0, 'Takemichi Hanagaki is a part-timer who lives in a crappy apartment and works at a job where his six-years-younger boss treats him like an idiot. One day, while watching the news, he finds out that the only girlfriend he ever had, Hinata Tachibana from middle school, was killed by the infamous Tokyo Manji Gang.', 6.5, 1, 'Ken Wakui')
INSERT INTO mangas VALUES (5, 'Viland Saga', '19/05/2022', 1, 'https://cdn.discordapp.com/attachments/766013915536556056/1103970557571117118/vs_01.jpg', 0, "Taking place in the 11th century, the story chronicles the life of Thorfinn Thorsson, an Icelandic boy who becomes a warrior to avenge his father's death. Dogging at the heels of his father's killer, Askeladd, Thorfinn winds up joining the Viking's band.", 6.5, 1, 'Makoto Yukimura')
INSERT INTO mangas VALUES (6, 'Blue Lock', '21/06/2022', 2, 'https://cdn.discordapp.com/attachments/766013915536556056/1104367144415789197/bl-01.jpg', 0, 'In order to claim victory in the World Cup, the Japanese Football Association decides to enlist the help of an eccentric coach called Jinpachi Ego. He decides that the proper course of action is to raise an egotistical striker with unparalleled hunger for scoring goals.', 6.5, 1, 'Muneyuki Kaneshiro')
